import React, { useEffect, useRef } from 'react';
import { motion, useAnimation } from 'framer-motion';
import { useInView } from 'react-intersection-observer';
import gsap from 'gsap';
import { ScrollTrigger } from 'gsap/ScrollTrigger';

gsap.registerPlugin(ScrollTrigger);

const Skills = () => {
  const controls = useAnimation();
  const [ref, inView] = useInView({
    threshold: 0.2,
    triggerOnce: true,
  });

  const sectionRef = useRef(null);
  const cardsRef = useRef(null);

  useEffect(() => {
    if (inView) {
      controls.start('visible');
    }
  }, [controls, inView]);

  useEffect(() => {
    const section = sectionRef.current;
    const cards = cardsRef.current;

    // Parallax effect for cards
    gsap.fromTo(
      cards.children,
      { y: 0 },
      {
        y: -30,
        stagger: 0.1,
        scrollTrigger: {
          trigger: section,
          start: 'top bottom',
          end: 'bottom top',
          scrub: true,
        },
      }
    );

    return () => {
      ScrollTrigger.getAll().forEach(trigger => trigger.kill());
    };
  }, []);

  const containerVariants = {
    hidden: { opacity: 0 },
    visible: {
      opacity: 1,
      transition: {
        staggerChildren: 0.1,
      },
    },
  };

  const itemVariants = {
    hidden: { y: 20, opacity: 0 },
    visible: {
      y: 0,
      opacity: 1,
      transition: {
        duration: 0.5,
        ease: [0.43, 0.13, 0.23, 0.96],
      },
    },
  };

  const technicalSkills = [
    { name: 'AWS', level: 85 },
    { name: 'Python', level: 90 },
    { name: 'MySQL', level: 80 },
    { name: 'Docker', level: 75 },
    { name: 'VM Ware', level: 70 },
    { name: 'Git & GitHub', level: 85 },
  ];

  const softSkills = [
    { name: 'Teamwork', level: 90 },
    { name: 'Communication', level: 85 },
    { name: 'Problem-Solving', level: 95 },
    { name: 'Adaptability', level: 90 },
    { name: 'Flexibility', level: 85 },
  ];

  const certificates = [
    { name: 'The Complete Python Bootcamp', issuer: 'Udemy', duration: '22 hours' },
    { name: 'AWS', issuer: 'Prepinsta', duration: '20+ hours' },
  ];

  return (
    <section
      id="skills"
      ref={sectionRef}
      className="py-20 md:py-32 bg-primary relative overflow-hidden"
    >
      {/* Background Elements */}
      <div className="absolute top-0 left-0 w-full h-full overflow-hidden z-0">
        <div className="absolute top-1/3 left-1/4 w-72 h-72 rounded-full bg-tertiary/10 blur-3xl"></div>
        <div className="absolute bottom-1/4 right-1/3 w-64 h-64 rounded-full bg-secondary/5 blur-3xl"></div>
      </div>

      <div className="container mx-auto px-4 md:px-6 relative z-10">
        <motion.div
          ref={ref}
          variants={containerVariants}
          initial="hidden"
          animate={controls}
          className="text-center mb-16"
        >
          <motion.h2 variants={itemVariants} className="text-2xl font-mono text-secondary mb-2">
            My Skills
          </motion.h2>
          <motion.h3 variants={itemVariants} className="text-4xl md:text-5xl font-bold">
            Expertise & Capabilities
          </motion.h3>
        </motion.div>

        <div className="grid grid-cols-1 md:grid-cols-2 gap-12">
          {/* Technical Skills */}
          <motion.div
            variants={containerVariants}
            initial="hidden"
            animate={controls}
          >
            <motion.h4 variants={itemVariants} className="text-2xl font-bold mb-8 text-textPrimary">
              Technical Skills
            </motion.h4>
            <motion.div variants={itemVariants} ref={cardsRef} className="space-y-6">
              {technicalSkills.map((skill, index) => (
                <motion.div
                  key={index}
                  variants={itemVariants}
                  className="bg-tertiary/30 backdrop-blur-sm rounded-lg p-6 border border-quaternary/30 hover:border-secondary/30 transition-all duration-300 transform hover:-translate-y-1 hover:shadow-lg"
                >
                  <div className="flex justify-between items-center mb-3">
                    <h5 className="text-lg font-semibold text-textPrimary">{skill.name}</h5>
                    <span className="text-secondary">{skill.level}%</span>
                  </div>
                  <div className="w-full bg-quaternary/50 rounded-full h-2.5">
                    <motion.div
                      className="bg-secondary h-2.5 rounded-full"
                      initial={{ width: 0 }}
                      animate={{ width: `${skill.level}%` }}
                      transition={{ duration: 1, delay: 0.3 + index * 0.1 }}
                    ></motion.div>
                  </div>
                </motion.div>
              ))}
            </motion.div>
          </motion.div>

          {/* Soft Skills */}
          <motion.div
            variants={containerVariants}
            initial="hidden"
            animate={controls}
          >
            <motion.h4 variants={itemVariants} className="text-2xl font-bold mb-8 text-textPrimary">
              Soft Skills
            </motion.h4>
            <motion.div variants={itemVariants} className="grid grid-cols-1 gap-6">
              {softSkills.map((skill, index) => (
                <motion.div
                  key={index}
                  variants={itemVariants}
                  className="bg-tertiary/30 backdrop-blur-sm rounded-lg p-6 border border-quaternary/30 hover:border-secondary/30 transition-all duration-300 transform hover:-translate-y-1 hover:shadow-lg"
                >
                  <div className="flex justify-between items-center mb-3">
                    <h5 className="text-lg font-semibold text-textPrimary">{skill.name}</h5>
                    <span className="text-secondary">{skill.level}%</span>
                  </div>
                  <div className="w-full bg-quaternary/50 rounded-full h-2.5">
                    <motion.div
                      className="bg-secondary h-2.5 rounded-full"
                      initial={{ width: 0 }}
                      animate={{ width: `${skill.level}%` }}
                      transition={{ duration: 1, delay: 0.3 + index * 0.1 }}
                    ></motion.div>
                  </div>
                </motion.div>
              ))}
            </motion.div>

            {/* Additional Skills */}
            <motion.div
              variants={itemVariants}
              className="mt-10 bg-tertiary/20 backdrop-blur-sm rounded-lg p-6 border border-quaternary/30"
            >
              <h4 className="text-xl font-bold mb-4 text-textPrimary">Additional Skills</h4>
              <div className="flex flex-wrap gap-3">
                {['React.js', 'Figma', 'Prometheus', 'Grafana', 'RHEL', 'Falco'].map((skill, index) => (
                  <motion.span
                    key={index}
                    initial={{ opacity: 0, scale: 0.8 }}
                    animate={{ opacity: 1, scale: 1 }}
                    transition={{ duration: 0.3, delay: 0.5 + index * 0.1 }}
                    className="px-4 py-2 bg-quaternary/50 text-textPrimary rounded-full text-sm border border-quaternary/70 hover:border-secondary/50 transition-colors duration-300"
                  >
                    {skill}
                  </motion.span>
                ))}
              </div>
            </motion.div>
          </motion.div>
        </div>

        {/* Certificates Section */}
        <motion.div
          initial={{ opacity: 0, y: 50 }}
          whileInView={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.5, delay: 0.6 }}
          viewport={{ once: true }}
          className="mt-16"
        >
          <h3 className="text-2xl font-bold text-textPrimary mb-6 text-center">Certifications</h3>
          <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
            {certificates.map((cert, index) => (
              <motion.div
                key={index}
                initial={{ opacity: 0, y: 20 }}
                whileInView={{ opacity: 1, y: 0 }}
                transition={{ duration: 0.3, delay: 0.1 * index }}
                viewport={{ once: true }}
                className="bg-tertiary p-6 rounded-lg shadow-lg hover:shadow-xl transition-all duration-300"
                whileHover={{ y: -5 }}
              >
                <h4 className="text-xl font-semibold text-textPrimary mb-2">{cert.name}</h4>
                <p className="text-textSecondary mb-1">Issuer: {cert.issuer}</p>
                <p className="text-textSecondary">Duration: {cert.duration}</p>
              </motion.div>
            ))}
          </div>
        </motion.div>
      </div>
    </section>
  );
};

export default Skills;
